mapper.update:
UPDATE EVENT_APPLICATION SET EVENT_CODE = #{eventCode}, STAT_CODE = #{statCode}, APP_TITLE = #{appTitle}, APP_SUB_TITLE = #{appSubTitle}, APP_DETAIL = #{appDetail}, LOCATION = #{location}, LOCATION_DETAIL = #{locationDetail}, POST_CODE = #{postCode}, WEBSITE = #{website}, START_DATE = #{startDate}, END_DATE = #{endDate}, START_TIME = #{startTime}, END_TIME = #{endTime}, UPDATE_DATE = SYSDATE,
SUBMITTED_DATE = SYSDATE,
APP_FEE = #{appFee}, APP_ITEM = #{appItem}, APP_HOST = #{appHost}, APP_ORG = #{appOrg}, APP_SPONSOR = #{appSponsor} WHERE APP_ID = #{appId}

		UPDATE APP_MANAGER SET
        MANAGER_NAME = #{appManager.managerName},
        EMAIL = #{appManager.email},
        TEL = #{appManager.tel},
        FAX = #{appManager.fax}
		WHERE APP_ID = #{appManager.appId}

mapper.insert:
INSERT INTO IMAGE(IMG_TYPE, ORIGIN_NAME, CHANGE_NAME, REF_BNO, IMG_ORDER)
FROM (

		SELECT
			#{Img.imgType},#{Img.originName}, #{Img.changeName}, #{Img.refNo}, #{Img.imgOrder}
		FROM DUAL

) C

mapper.select:
		SELECT A.*, B.APP_ID, B.MANAGER_NAME, B.EMAIL, B.TEL, B.FAX
    	FROM (
	    	SELECT EA.*, ROWNUM RNUM
		    FROM (
		        SELECT * FROM EVENT_APPLICATION
		        ORDER BY CREATE_DATE DESC
	    	) EA
    	) A LEFT JOIN APP_MANAGER B
    	ON A.APP_ID = B.APP_ID
        WHERE A.RNUM BETWEEN #{offset} + 1 AND #{offset} + #{offset} + #{limit}

