mapper.select:
SELECT * FROM (
SELECT ROWNUM RNUM, N.* FROM (
SELECT NOTICE_ID, NOTICE_TITLE, NOTICE_DETAIL, CREATE_DATE, UPDATE_DATE,
HIGHLIGHT
FROM NOTICE
ORDER BY NOTICE_ID DESC
) N
WHERE ROWNUM <= #{endRow}
)
WHERE RNUM > #{startRow}

mapper.insert:
INSERT INTO NOTICE (
NOTICE_TITLE,
NOTICE_DETAIL
) VALUES (
#{noticeTitle},
#{noticeDetail}
)
