mapper.update:
UPDATE USERS
SET USER_PWD = #{userPwd}
WHERE EMAIL = #{email}

UPDATE USERS SET USER_PWD = #{userPwd}
               , NICKNAME = #{nickname}
               , UPDATE_DTAE = SYSDATE
WHERE USER_NO = #{userNo}

UPDATE USERS SET STATUS = 'T'
               , UPDATE_DTAE = SYSDATE
WHERE USER_NO = #{userNo}

